import OrgForm from "./components/Form/OrgForm";
import LandingPage from "./components/ProfilePage/LandingPage/LandingPage";
import { Fragment, useEffect } from "react";
import { useSelector } from "react-redux";

function App() {
  const formValid = useSelector((state) => state.form.totalSubmit);
  const userData = useSelector((state) => state.form.username);

  const bool = userData !== "" ? true : false;

  return (
    <Fragment>
      <div className="app">
        {formValid && bool  &&<OrgForm />}
        {!formValid && !bool && <LandingPage />}
      </div>
    </Fragment>
  );
}

export default App;
