import BasicForm from "./components/Form/OrgForm";
import { Fragment } from "react";
import { useSelector } from "react-redux";

function App() {
  const formValid = useSelector((state) => state.form.formValid);
  return (
    <Fragment>
      <div className="app">{!formValid && <BasicForm />}</div>;
    </Fragment>
  );
}

export default App;
