import { createSlice } from "@reduxjs/toolkit";

const mainPageSlice = createSlice({
  name: "task",
  initialState: {
    exit: logout,
  },
  reducers: {
    logout(state) {
      state.logout = false;
    },
  },
});

export const mPageActions = mainPageSlice.actions; 

export default mainPageSlice;
