import useInputForm from "../hooks/use-inputform";

const BasicForm = (props) => {
  const {
    value: firstName,
    valueIsInValid: firstNameIsInValid,
    errorChecker: totalInValid,
    reset: firstNameReset,
    blurHandler: firstNameErrorHandler,
    enteredValueHandler: firstNameInputHandler,
  } = useInputForm((value) => value.length > 6);

  const formSubmissionHandler = (e) => {
    e.preventDefault();
    if (!email && !firstNameIsInValid && !lastName) {
      return;
    }

    firstNameReset();
  };

  const firstNameValidClass = totalInValid
    ? "form-control"
    : "form-control invalid";

  return (
    <form onSubmit={formSubmissionHandler}>
      <div className="control-group">
        <div className={firstNameValidClass}>
          <label htmlFor="name">First Name</label>
          <input
            type="text"
            id="name"
            onChange={firstNameInputHandler}
            onBlur={firstNameErrorHandler}
            value={firstName}
          />
          <p>{totalInValid && "Please fill the input box"}</p>
        </div>
        <div className="form-control">
          <label htmlFor="name">Last Name</label>
          <input
            type="text"
            id="name"
            onChange={lastNameInputHandler}
            onBlur={lastNameErrorHandler}
            value={lastName}
          />
          <p>{firstNameIsValid && "Please fill the input box"}</p>
        </div>
      </div>
      <div className="form-control">
        <label htmlFor="name">E-Mail Address</label>
        <input
          type="email"
          id="name"
          onChange={emailInputHandler}
          onBlur={emailErrorHandler}
          value={email}
        />
        <p>{emailIsValid && "Please fill the input box"}</p>
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
