import useInputForm from "../hooks/use-inputform";
import "../index.css";
const BasicForm = (props) => {
  const {
    value: firstName,
    valueIsInValid: firstNameIsInValid,
    errorChecker: firtstNameTotalInValid,
    reset: firstNameReset,
    blurHandler: firstNameErrorHandler,
    enteredValueHandler: firstNameInputHandler,
  } = useInputForm((value) => value.length > 6);

  const {
    value: lastName,
    valueIsInValid: lastNameIsInValid,
    errorChecker: lastNameTotalInValid,
    reset: lastNameReset,
    blurHandler: lastNameErrorHandler,
    enteredValueHandler: lastNameInputHandler,
  } = useInputForm((value) => value.length > 6);

  const formValid = firstNameIsInValid && lastNameIsInValid;

  const formSubmissionHandler = (e) => {
    e.preventDefault();
    if (!lastNameIsInValid || !firstNameIsInValid) {
      return;
    }

    firstNameReset();
    lastNameReset();
  };

  const firstNameValidClass = firtstNameTotalInValid
    ? "form-control invalid"
    : "form-control";

  const lastNameValidClass = lastNameTotalInValid
    ? "form-control invalid"
    : "form-control";

  return (
    <form onSubmit={formSubmissionHandler}>
      <div className="control-group">
        <div className={firstNameValidClass}>
          <label htmlFor="name">First Name</label>
          <input
            type="text"
            id="name"
            onChange={firstNameInputHandler}
            onBlur={firstNameErrorHandler}
            value={firstName}
          />
          <p className="error-text">
            {firtstNameTotalInValid && "Please fill the input box"}
          </p>
        </div>
        <div className="form-control">
          <label htmlFor="name">Last Name</label>
          <input
            type="text"
            id="name"
            onChange={lastNameInputHandler}
            onBlur={lastNameErrorHandler}
            value={lastName}
          />
          <p  className="error-text">{lastNameTotalInValid && "Please fill the input box"}</p>
        </div>
      </div>
      {/* <div className="form-control">
        <label htmlFor="name">E-Mail Address</label>
        <input
          type="email"
          id="name"
          onChange={emailInputHandler}
          onBlur={emailErrorHandler}
          value={email}
        />
        <p>{emailIsValid && "Please fill the input box"}</p>
      </div> */}
      <div className="form-actions">
        <button disabled={!formValid}>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
