import "./TaskLists.css";
import taskLists from "../../../mockdatas/tasklists";
import { Fragment } from "react";
import { useSelector } from "react-redux";

const ToDo = () => {
  const todoTasks = taskLists.filter((task) => task.taskPosition === "todo");
  const newTask = useSelector((state) => state.createTaskSlice.task);
  
  return (
    <Fragment>
      {todoTasks.map((task) => (
        <div key={task.id} className="taskItem">
          <div className="taskDetails">
            <p>{task.description}</p>
            <div className="taskName">{task.taskName}</div>
            <footer>
              <h6>
                DEADLINE <span>{task.deadline}</span>
              </h6>
              <h6>
                {task.taskGivenTo}
              </h6>
            </footer>
          </div>
        </div>
      ))}
      {newTask.map((task) => (
        <div key={task.title.substring(0,2)} className="taskItem">
          <div className="taskDetails">
            <p>{task.textarea}</p>
            <div className="taskName">{task.title}</div>
            <footer>
              <h6>
                DEADLINE <span>{task.deadline}</span>
              </h6>
              <h6>{task.taskGivenTo}</h6>
            </footer>
          </div>
        </div>
      ))}
    </Fragment>
  );
};

export default ToDo;
