import OrgForm from "./components/Form/OrgForm";
import LandingPage from "./components/ProfilePage/LandingPage/LandingPage";
import { Fragment, useEffect, useState } from "react";
import { useSelector } from "react-redux";

function App() {
  let formValid = useSelector((state) => state.form.totalSubmit);
  const [item, setItem] = useState(null);

  console.log(formValid);
  useEffect(() => {
    setItem(() => localStorage.getItem("key"));
    if (item !== "") {
      formValid = false;
    }
  }, [item]);

  return (
    <Fragment>
      <div className="app">
        {formValid && <OrgForm />}
        {!formValid && <LandingPage />}
      </div>
    </Fragment>
  );
}

export default App;
