import OrgForm from "./components/Form/OrgForm";
import LandingPage from './components/ProfilePage/LandingPage/MainPage'
import { Fragment } from "react";
import { useSelector } from "react-redux";

function App() {
  const formValid = useSelector((state) => state.form.formValid);
  return (
    <Fragment>
      <div className="app">
        {formValid && <OrgForm />} 
        {!formValid && <LandingPage/>}
      </div>
    </Fragment>
  );
}

export default App;
