import { createSlice } from "@reduxjs/toolkit";

const createTask = createSlice({
  name: "createTask",
  initialState:{ task: [], taskAuth: false },
  reducers: {
    addTask(state, actions) {
      const taskDetails = actions.payload;
      state.task.push(taskDetails)
      console.log(taskDetails)
      if(taskDetails.title.length < 3){
        state.taskAuth = false;
      }else{
        state.taskAuth = true;
      }  
    },
    changeTaskAuth(state,actions) {
      const changes = actions.payload;
      state.taskAuth = changes;
    }
  },
});

export const createTaskActions = createTask.actions;

export default createTask;
