import "./TaskLists.css";
import taskLists from "../../../mockdatas/tasklists";
import { Fragment } from "react";
import { useSelector, useDispatch } from "react-redux";
import { createTaskActions } from "../../../store/createtask-slice";
const InProgress = () => {
  const dispatch = useDispatch()
  const inProgressTasks = taskLists.filter(
    (task) => task.taskPosition === "inprogress"
  );
  const newTask = useSelector((state) => state.createTaskSlice.task);
  const todoNewTask = newTask.filter((task) => task.taskPosition === "inprogress");

  const doneHandler = () => {
    dispatch(createTaskActions.changeToDone())
  }

  return (
    <Fragment>
      {inProgressTasks.map((task) => (
        <div key={task.id} className="taskItem">
          <div className="taskDetails">
            <p>{task.description}</p>
            <div className="taskName">{task.taskName}</div>
            <div className="footer">
              <h6>
                DEADLINE <span>{task.deadline}</span>
              </h6>
              <h6>{task.taskGivenTo}</h6>
            </div>
            <footer>
              <input type="checkbox" className=""/>
            </footer>
          </div>
        </div>
      ))}
      {todoNewTask.map((task) => (
        <div key={task.id} className="taskItem">
          <div className="taskDetails">
            <p>{task.description}</p>
            <div className="taskName">{task.taskName}</div>
            <div className="footer">
              <h6>
                DEADLINE <span>{task.deadline}</span>
              </h6>
              <h6>{task.taskGivenTo}</h6>
            </div>
            <footer>
              <input onClick={(e) => doneHandler()}>Done</input>
            </footer>
          </div>
        </div>
      ))}
    </Fragment>
  );
};

export default InProgress;
