import "./TaskLists.css";
import taskLists from "../../../mockdatas/tasklists";
import { Fragment } from "react";
import { useSelector, useDispatch } from "react-redux";
import { createTaskActions } from "../../../store/createtask-slice";

const ToDo = () => {
  const todoTasks = taskLists.filter((task) => task.taskPosition === "todo");
  const newTask = useSelector((state) => state.createTaskSlice.task);
  const todoNewTask = newTask.filter((task) => task.taskPosition === "todo");

  return (
    <Fragment>
      {todoTasks.map((task) => (
        <div key={task.id} className="taskItem">
          <div className="taskDetails">
            <p>{task.description}</p>
            <div className="taskName">{task.taskName}</div>
            <div className="footer">
              <h6>
                DEADLINE <span>{task.deadline}</span>
              </h6>
              <h6>{task.taskGivenTo}</h6>
            </div>
            <footer>
              <a>In Progress</a>
              <a>Done</a>
            </footer>
          </div>
        </div>
      ))}
      {todoNewTask.map((task) => (
        <div key={task.title.substring(0, 2)+1} className="taskItem">
          <div className="taskDetails">
            <p>{task.textarea}</p>
            <div className="taskName">{task.title}</div>
            <div className="footer">
              <h6>
                DEADLINE <span>{task.deadline}</span>
              </h6>
              <h6>{task.taskGivenTo}</h6>
            </div>
            <footer>
            <input
                type="checkbox"
                value={task.id}
                onClick={(e) => doneHandler()}
              />
            </footer>
          </div>
        </div>
      ))}
    </Fragment>
  );
};

export default ToDo;
