import { Fragment } from "react";
import Header from "./Header.js";
import Card from "../../UI/Card";
import Main from "./Main";
import TaskList from "../TaskSection/TaskLists";
import { useSelector, useDispatch } from "react-redux";
import { createTaskActions } from "../../../store/createtask-slice";
import Create from "../CreateTask/Create";

const LandingPage = () => {
  let value = useSelector((state) => state.mainPageSlice.pageRoute);
  let taskAuthValue = useSelector((state) => state.createTaskSlice.taskAuth);
  const dispatch = useDispatch();
  if (taskAuthValue) {
    value = taskAuthValue;
    setTimeout(() => dispatch(createTaskActions.changeTaskAuth(false)), 2000);
  }

  return (
    <Fragment>
      <Header />
      <Card>
        {value && (
          <div>
            <Main />
            <TaskList />
          </div>
        )}
        {!value && <Create />}
      </Card>
    </Fragment>
  );
};

export default LandingPage;
