import { useRef } from "react";
import { useDispatch } from "react-redux";
import { createTaskActions } from "../../../store/createtask-slice";
import Card from "../../UI/Card";
import "./Create.css";

const Create = () => {
  const title = useRef();
  const deadline = useRef();
  const textarea = useRef();

  return (
    <Card>
      <div className="taskContainer2">
        <div className="taskHeader">
          <div className="title">
            <label htmlFor="title">Title</label>
            <input type="text" id="title" placeholder="Title" ref={title} />
          </div>
          <div className="title">
            <label htmlFor="title">Deadline</label>
            <input type="text" id="title" placeholder="Title" ref={deadline} />
          </div>
        </div>
        <div className="textarea">
          <textarea
            ref={textarea}
            name="taskDescription"
            id="taskDescription"
            cols="0"
            rows="0"
          ></textarea>
        </div>
        <div className="btns">
          <button>Share Task</button>
        </div>
      </div>
    </Card>
  );
};

export default Create;
