import { useState } from "react";

const useInput = (validate) => {
  const [valueIsValid, setValueIsValid] = useState(false);
  const [enteredValue, setEnteredValue] = useState("");

  const hasError = setValueIsValid(validate);
  const checker = !hasError && valueIsValid;

  const enteredValueHandler = (e) => {
    setEnteredValue(e.target.value);
  };

  const isBlurHandler = () => {
    serHasError(true);
  };

  const reset = () => {
    setEnteredValue("");
    setValueIsValid(false);
  };

  return {
    value: enteredValue,
    valueIsInValid: hasError,
    errorChecker: checker,
    reset: reset,
    blurHandler: isBlurHandler,
    enteredValueHandler: enteredValueHandler,
  };
};

export default useInput;
