import { createSlice } from "@reduxjs/toolkit";

const initialState = { formValid: true };
const formSlice = createSlice({
  name: "form",
  initialState,
  reducers: {
    isValid(state, action) {
      state.formValid =
        action.usernameIsInValid &&
        action.passwordIsInValid &&
        action.emailIsInvalid &&
        action.addressIsInvalid &&
        action.phoneIsInvalid &&
        action.orgnameIsInvalid;
    },
  },
});

export const formActions = formSlice.actions;

export default formSlice;
